import{_ as a,c as i,o as t,ag as s}from"./chunks/framework.B-XtCDNB.js";const k=JSON.parse('{"title":"麒麟服务器V10系统激活","description":"银河麒麟服务器操作系统的激活方法","frontmatter":{"title":"麒麟服务器V10系统激活","description":"银河麒麟服务器操作系统的激活方法","lastUpdated":"2025-10-17T12:49:20.000Z","outline":"deep"},"headers":[],"relativePath":"docs/kylin/kylin-activation.md","filePath":"docs/kylin/kylin-activation.md","lastUpdated":1760705360000}'),n={name:"docs/kylin/kylin-activation.md"};function p(o,e,l,d,c,r){return t(),i("div",null,[...e[0]||(e[0]=[s('<h2 id="_0x01-激活方式一-图形化界面激活" tabindex="-1">0x01 激活方式一：图形化界面激活 <a class="header-anchor" href="#_0x01-激活方式一-图形化界面激活" aria-label="Permalink to &quot;0x01 激活方式一：图形化界面激活&quot;">​</a></h2><p>1.右键点击我的电脑-&gt;选择属性</p><p>2.在&#39;我的电脑属性&#39;页面中&#39;激活信息&#39;点击&#39;激活&#39;</p><p>3.图行化界面导入授权文件</p><blockquote><p>小数点开头的文件是隐藏文件，<code>.kyinfo</code>是隐藏文件，需要右键空白处，勾选“显示隐藏文件” 或者按ctrl+H快捷键显示隐藏文件。</p></blockquote><p>4.二维码扫码激活（可以离线，离线可以输入激活码）</p><p>5.输入服务序列号，用有激活权限的微信扫码，可以获取到激活码；</p><h2 id="_0x02-激活方式二-命令行方式激活" tabindex="-1">0x02 激活方式二：命令行方式激活 <a class="header-anchor" href="#_0x02-激活方式二-命令行方式激活" aria-label="Permalink to &quot;0x02 激活方式二：命令行方式激活&quot;">​</a></h2><p>1.准备工作：将商务申请的<code>.kyinfo</code>及<code>LICENSE</code>授权文件拷贝至<code>/etc</code>目录下</p><div class="language-bash vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">cp</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">   .kyinfo</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">  LICENSE</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">   /etc</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>2.登录到物理服务器上，在终端下输入 <code>sudo kylin-system-verify</code> 命令显示系统信息，按回车键继续。</p><p>3.按提示输入kylin，按回车键继续。</p><p>4.生成二维码——拍照——回车（用绑定了管理员权限的微信，扫这个二维码，填入授权书上的验证码后，可获取到激活码。或者把二维码发给麒麟工程师生成激活码）</p><p>5.输入获取到的激活码，回车</p><p>6.激活完成。</p>',15)])])}const _=a(n,[["render",p]]);export{k as __pageData,_ as default};
